function TianTiRobotConfig.CreateRobot()
    for i = 1, TianTiRobotConfig.RobotNum do
        sLuaTimerSystem:AddDelayer(50,"TianTiRobotConfig.RobotData",""..i)
    end
    if TianTi then
        TianTi.RefreshTianTiRank() 
    end
end

function TianTiRobotConfig.RobotData(player,rank)
    local role = {}
    local pets = {}
    local guards = {}

    if TianTiRobotConfig.TianTiRobot_role then--随机角色
        local RandRole = sLuaApp:RandInteger(1,#TianTiRobotConfig.TianTiRobot_role)
        if RandRole <= 0 then
            return
        end
        local RoleId = TianTiRobotConfig.TianTiRobot_role[RandRole]["Id"]
        role['roleid'] = RoleId
        role['level'] = TianTiRobotConfig.RobotLevel
        role['reincarnation'] = TianTiRobotConfig.Reincarnation
        role['seat_id'] = TianTiRobotConfig.SeatID[sLuaApp:RandInteger(1,#TianTiRobotConfig.SeatID)]
        role['seat_level'] = TianTiRobotConfig.SeatLevel
        role['seat_score'] = 0
        role['attrs'] = {}
        role['skills'] = {}
        for k, v in pairs(TianTiRobotConfig.TianTiRobot_role[RandRole]["AttrIniValue"]) do --设置属性
            if k == 321 then --武器ID
                table.insert(role['attrs'],k)
                local RandID = sLuaApp:RandInteger(1,#TianTiRobotConfig.WeaponID[RoleId])
                table.insert(role['attrs'],TianTiRobotConfig.WeaponID[RoleId][RandID])
            elseif k == 3 then
                role['school'] = v
            else
                table.insert(role['attrs'],k)
                local AttrValue = ( v + (1000-rank) * TianTiRobotConfig.RoleIncConfig[k] )
                table.insert(role['attrs'],math.ceil(AttrValue))
            end
        end
        
        for k, v in pairs(TianTiRobotConfig.TianTiRobot_role[RandRole]["Skill"]) do--设置技能
            local SkillLevel = TianTiRobotConfig.TianTiRobot_role[RandRole]["SkillLevel"]
            table.insert(role['skills'] , v)
            local SkillLevelValue = ( SkillLevel + (1000-rank) * TianTiRobotConfig.RoleIncConfig["SkillLevel"] ) / 10
            table.insert(role['skills'] , math.ceil(SkillLevelValue))
        end
    end
    --sLuaApp:LuaDbg("role ----------- "..Lua_tools.serialize(role))

    if TianTiRobotConfig.TianTiRobot_pet then --随机宠物
        local RandIndexList = {}
        local IndexList = {}
        for key, value in pairs(TianTiRobotConfig.TianTiRobot_pet) do --得到宠物的索引表
            IndexList[key] = key
        end
        RandIndexList = TianTiRobotConfig.GetArrayRand(TianTiRobotConfig.PetNum,IndexList)--得到随机的5个不相同宠物索引

        for a = 1, TianTiRobotConfig.PetNum do
            local pet = {}
            local RandIndex = RandIndexList[a]
            if RandIndex <= 0 then
                return
            end
            pet['roleid'] = TianTiRobotConfig.TianTiRobot_pet[RandIndex]["Id"]
            pet['level'] = TianTiRobotConfig.RobotLevel
            pet['reincarnation'] = TianTiRobotConfig.Reincarnation
            pet['attrs'] = {}
            pet['skills'] = {}
            for k, v in pairs(TianTiRobotConfig.TianTiRobot_pet[RandIndex]["AttrIniValue"]) do --设置属性
                table.insert(pet['attrs'],k)
                local AttrValue = ( v + (1000-rank) * TianTiRobotConfig.PetIncConfig[k] )
                table.insert(pet['attrs'],math.ceil(AttrValue))
            end
            --for k, v in pairs(TianTiRobotConfig.TianTiRobot_pet[RandIndex]["Skill"]) do--设置技能
            --    table.insert(pet['skills'],v)
            --    local SkillLevel = TianTiRobotConfig.TianTiRobot_pet[RandIndex]["SkillLevel"]
            --    local SkillLevelValue = ( SkillLevel + (1000-rank) * TianTiRobotConfig.PetIncConfig["SkillLevel"] ) / 10
            --    table.insert(pet['skills'],math.ceil(SkillLevelValue))
            --end
            table.insert(pets,pet)
            --pets['pet'..a] = pet
        end
    end
    --sLuaApp:LuaDbg("pets ----------- "..Lua_tools.serialize(pets))


    if TianTiRobotConfig.TianTiRobot_guard then --随机侍从
        local RandIndexList = {}
        local IndexList = {}
        for key, value in pairs(TianTiRobotConfig.TianTiRobot_guard) do --得到侍从的索引表
            IndexList[key] = key
        end
        RandIndexList = TianTiRobotConfig.GetArrayRand(TianTiRobotConfig.GuardNum,IndexList)--得到随机的4个不相同侍从索引
        for a = 1, TianTiRobotConfig.GuardNum do
            local guard = {}
            local RandRole = RandIndexList[a]
            if RandRole <= 0 then
                return
            end
            guard['roleid'] = TianTiRobotConfig.TianTiRobot_guard[RandRole]["Id"]
            guard['level'] = TianTiRobotConfig.RobotLevel
            guard['reincarnation'] = TianTiRobotConfig.Reincarnation
            guard['attrs'] = {}
            guard['skills'] = {}
            for k, v in pairs(TianTiRobotConfig.TianTiRobot_guard[RandRole]["AttrIniValue"]) do --设置属性
                table.insert(guard['attrs'],k)
                local AttrValue = ( v + (1000-rank) * TianTiRobotConfig.GuardIncConfig[k] )
                table.insert(guard['attrs'],math.floor(AttrValue))
            end
            --for k, v in pairs(TianTiRobotConfig.TianTiRobot_guard[RandRole]["Skill"]) do--设置技能
            --    table.insert(guard['skills'],v)
            --    local SkillLevelValue = 0
            --    if v ~= 0 then
            --        local SkillLevel = TianTiRobotConfig.TianTiRobot_guard[RandRole]["SkillLevel"]
            --        SkillLevelValue = ( SkillLevel + (1000-rank) * TianTiRobotConfig.GuardIncConfig["SkillLevel"] ) / 10
            --    end
            --    table.insert(guard['skills'],math.floor(SkillLevelValue))
            --end
            table.insert(guards,guard)
            --guards['guard'..a] = guard
        end
    end
    --sLuaApp:LuaDbg("guards ----------- "..Lua_tools.serialize(guards))

    sLadderSystem:CreateLadder(role,pets,guards)
end

--从数组中取出指定数目并且不相同的随机数
function TianTiRobotConfig.GetArrayRand(num,array)
	for i = 1 , num do
		local rand = sLuaApp:RandInteger(1,#array)
		local tmp = array[i]
		array[i] = array[rand]
		array[rand] = tmp
	end
	local a = {}
	for i = 1 , num do
		a[i] = array[i]
	end
	return a
end